package com.lumen.basic;

public class PatternOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int range = 10;
		
		for (int i = 1; i <= range; i++) {
			for (int j = 1; j <= i; j++) {
				System.out.print(i);
				
			}
			System.out.print(" ");
			
		}

	}

}
